END;

BEGIN
	-- Vérifier si le status existe
	IF NOT EXISTS (
		SELECT 1 FROM [dbo].[Status] 
		WHERE [Id_Status] = @Id_Status
		)
	BEGIN
		RAISERROR ('Erreur : Status non trouvé.', 16, 1);
		RETURN;
